(()=>{const t=document.querySelector("[data-add-task-form]"),e=document.querySelector("[data-task-list]");t.addEventListener("submit",(t=>{t.preventDefault(),function(){const t=document.querySelector("[data-task-title]"),n=document.createElement("li");n.innerHTML=`\n  <div class="unchecked"></div>\n  <div class="list-info">\n    <h3 class="list-info-title">${t.value}</h3>\n    <h4 class="list-info-src">Test</h4>\n  </div>\n  `,e.appendChild(n)}()}))})();