(()=>{const e=document.querySelector("[data-todo-list]"),t=(document.querySelector("[data-add-todo-form]"),document.querySelector("[data-add-todo-text]")),o=document.querySelector("[data-add-todo-btn]"),n=document.querySelector("[data-item-template]");o.addEventListener("click",(function(o){if(o.preventDefault(),""===t.value)return alert("Task cannot be empty");if(""!==t.value){const o=n.content.cloneNode(!0),d=o.querySelector("#todo-title");d.innerText=t.value;const c=o.querySelector(".todo-btn-completed");c.addEventListener("click",(()=>{d.classList.toggle("done"),"Undo"===c.innerText?c.innerText="Completed":c.innerText="Undo"}));const r=o.querySelector(".todo-btn-delete");r.addEventListener("click",(()=>{const t=r.parentElement;e.removeChild(t)})),e.append(o),t.value=""}}))})();